mapper.select:
select 

from t_dev


    F_ID = #{fId,jdbcType=CHAR}


    AND F_ASSERTS = #{fAsserts,jdbcType=CHAR}


    AND F_USESTAT = #{fUsestat,jdbcType=CHAR}


    AND F_TYPE = #{fType,jdbcType=CHAR}


    AND F_MARK = #{fMark,jdbcType=CHAR}


    AND F_SPC = #{fSpc,jdbcType=CHAR}

SELECT COUNT (*)
FROM t_dev A LEFT OUTER JOIN t_codes C6 ON C6.F_CATID = 'C6' AND A.F_STYLE = C6.F_ID
LEFT OUTER JOIN t_codes C1 ON C1.F_CATID = 'C1' AND A.F_USESTAT = C1.F_ID
LEFT OUTER JOIN t_devstate S LEFT OUTER JOIN t_states SN ON S.F_STATEID = SN.F_STATEID AND S.F_VALUE = SN.F_VALUE
ON A.F_ID = S.F_DEVID AND (S.F_STATEID = 1001 OR S.F_STATEID = 2001)
, t_branch B, t_branch C


    A.F_DEPT=B.F_ID AND B.F_DEPT=C.F_ID AND B.F_DEPTID LIKE #{fDeptid}||'%'


    AND A.F_ASSERTS=#{fAsserts}


    AND A.F_TYPE=#{fType}


    AND A.F_MARK=#{fMark}


    AND A.F_SPC=#{fSpc}


    AND A.F_USESTAT=#{fUsestat}

SELECT B.F_NAME WDNAME, C.F_NAME ZHNAME, A.F_ASSERTS, A.F_ID, A.F_TERM, A.F_TYPE, A.F_MARK, A.F_SPC, A.F_INSTADDR, C6.F_NAME AS STYLENAME, A.F_USESTAT, C1.F_NAME AS USESTATNAME, SN.F_NAME AS STATENAME FROM t_dev A LEFT JOIN t_codes C6 ON C6.F_CATID = 'C6' AND A.F_STYLE = C6.F_ID LEFT JOIN t_codes C1 ON C1.F_CATID = 'C1' AND A.F_USESTAT = C1.F_ID LEFT JOIN t_devstate S LEFT JOIN t_states SN ON S.F_STATEID = SN.F_STATEID AND S.F_VALUE = SN.F_VALUE ON A.F_ID = S.F_DEVID AND (S.F_STATEID = 1001 OR S.F_STATEID = 2001) , t_branch B, t_branch C
A.F_DEPT=B.F_ID AND B.F_DEPT=C.F_ID AND B.F_DEPTID LIKE #{fDeptid}||'%'
AND A.F_ASSERTS=#{fAsserts}
AND A.F_TYPE=#{fType}
AND A.F_MARK=#{fMark}
AND A.F_SPC=#{fSpc}
AND A.F_USESTAT=#{fUsestat}
ORDER BY ${orderby}

SELECT A.F_ID,A.F_TYPE,B.F_VALUE STATEVALUE,C.F_NAME STATENAME,A.F_INST,A.F_INSTADDR,A.F_IP,A.F_MARK,A.F_SPC ,F.F_TEL factel,G.F_TEL isptel,F.F_NAME facname,G.F_NAME ispname,
DD.STATENAME, DD.VALUENAME, DD.F_COLOR, DD.F_STATEID, DD.F_VALUE, DD.F_DTIME
,D.F_NAME DEPTNAME,D.F_TEL branchtel
FROM t_dev A LEFT JOIN t_devstate B ON A.F_ID = B.F_DEVID AND B.F_STATEID IN ('1001','2001') LEFT JOIN t_states C ON B.F_STATEID = C.F_STATEID AND B.F_VALUE = C.F_VALUE LEFT JOIN t_factory F ON A.F_FACID = F.F_ID LEFT JOIN t_devisp G ON A.F_ISPID = G.F_ID,
(SELECT B.F_NAME STATENAME, C.F_NAME VALUENAME, C.F_PAD1 F_COLOR, A.F_STATEID, A.F_VALUE, A.F_DTIME , A.F_DEVID, CASE WHEN A.F_STATEID = 1001 THEN CONVERT('面额', CHAR) WHEN A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) THEN A.F_MDN ELSE '' END MDN, CASE WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) AND A.F_LOOPBOX = 0) THEN '废钞箱' WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) AND A.F_LOOPBOX = 1) THEN '取款箱' WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) AND A.F_LOOPBOX = 2) THEN '存款箱' WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) AND A.F_LOOPBOX = 3) THEN '循环箱' WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010) AND A.F_LOOPBOX = 4) THEN '回收箱' WHEN (A.F_STATEID IN (1001, 2001) AND F_DTIME IS NOT NULL AND F_DTIME != ' ') THEN CONVERT(CONCAT(SUBSTR(A.F_DTIME, 1, 4), '-', SUBSTR(A.F_DTIME, 5, 2), '-', SUBSTR(A.F_DTIME, 7, 2)), CHAR) END AS BOX, CASE WHEN (A.F_STATEID IN (1003, 1004, 1005, 1006, 1007, 1008, 1009, 1010, 2032)) THEN CONCAT(CONVERT(A.F_COUNT, CHAR), '张') WHEN (A.F_STATEID IN (1001, 2001) AND F_DTIME IS NOT NULL AND F_DTIME != ' ') THEN CONVERT(CONCAT(SUBSTR(A.F_DTIME, 9, 2), ':', SUBSTR(A.F_DTIME, 11, 2)), CHAR) END AS CNT FROM t_devstate A LEFT OUTER JOIN t_statecat B ON A.F_STATEID = B.F_ID LEFT OUTER JOIN t_states C ON A.F_STATEID = C.F_STATEID AND A.F_VALUE = C.F_VALUE
) DD
, t_branch D, t_branch E, t_branch H
A.F_ID = DD.F_DEVID
A.F_ID = #{fId}
AND A.F_DEPT=D.F_ID AND D.F_DEPT=E.F_ID AND E.F_DEPT = H.F_ID
AND D.F_DEPTID LIKE #{fDeptid}||'%'
AND A.F_TYPE=#{fType}
AND A.F_MARK=#{fMark}
AND A.F_SPC=#{fSpc}
AND B.F_VALUE IN
${fState}

SELECT A.F_ID,A.F_ASSERTS,A.F_TERM,A.F_TYPE,B.F_VALUE STATEVALUE,C.F_NAME STATENAME,A.F_INST,A.F_INSTADDR,A.F_IP,A.F_MARK,A.F_SPC
,F.F_TEL factel,G.F_TEL isptel,F.F_NAME facname,G.F_NAME ispname

  ,D.F_NAME DEPTNAME,D.F_TEL branchtel

FROM t_dev A
LEFT JOIN t_devstate B ON A.F_ID = B.F_DEVID AND B.F_STATEID IN ('1001','2001')
LEFT JOIN t_states C ON B.F_STATEID = C.F_STATEID AND B.F_VALUE = C.F_VALUE
LEFT JOIN t_factory F ON A.F_FACID = F.F_ID
LEFT JOIN t_devisp G ON A.F_ISPID = G.F_ID

, t_branch D, t_branch E



    A.F_ID = #{fId}


    A.F_DEPT=D.F_ID AND D.F_DEPT=E.F_ID AND D.F_DEPTID LIKE #{fDeptid}||'%'


    AND A.F_TYPE=#{fType}


    AND A.F_MARK=#{fMark}


    AND A.F_SPC=#{fSpc}


    AND B.F_VALUE IN

      ${fState}

SELECT A.*, D_1.F_NAME fWdname, D_2.F_NAME fZhname, D_3.F_NAME fFhname,
B.F_NAME fStylename, C.F_NAME fInststylename, D.F_NAME fFuncareaname, E.F_NAME fAreaname, F.F_NAME fIspname, G.F_NAME fFacname,
J.F_VALUE fStatevalue, K.F_NAME fStatename,
CASE WHEN A.F_LASTTS IS NOT NULL AND A.F_LASTTS != '' THEN SUBSTR(A.F_LASTTS, 1, 4)|| '-'|| SUBSTR(A.F_LASTTS, 5, 2)|| '-'|| SUBSTR(A.F_LASTTS, 7, 2)
ELSE '' END AS LASTTS1, CASE WHEN A.F_LASTTS IS NOT NULL AND A.F_LASTTS != '' THEN SUBSTR(A.F_LASTTS, 9, 2)|| ':'|| SUBSTR(A.F_LASTTS, 11, 2) ELSE '' END AS LASTTS2, CASE WHEN A.F_LASTST IS NOT NULL AND A.F_LASTST != '' THEN SUBSTR(A.F_LASTST, 1, 4)|| '-'|| SUBSTR(A.F_LASTST, 5, 2)|| '-'|| SUBSTR(A.F_LASTST, 7, 2) ELSE '' END AS LASTST1, CASE WHEN A.F_LASTST IS NOT NULL AND A.F_LASTST != '' THEN SUBSTR(A.F_LASTST, 9, 2)|| ':'|| SUBSTR(A.F_LASTST, 11, 2) ELSE '' END AS LASTST2 FROM t_dev A
LEFT OUTER JOIN t_factory G ON A.F_FACID = G.F_ID LEFT OUTER JOIN t_devisp F ON A.F_ISPID = F.F_ID LEFT OUTER JOIN t_codes E ON E.F_CATID = 'C3' AND A.F_AREA = E.F_ID LEFT OUTER JOIN t_codes D ON D.F_CATID = 'CA' AND A.F_FUNCAREA = D.F_ID LEFT OUTER JOIN t_codes C ON C.F_CATID = 'C5' AND A.F_INSTSTYLE = C.F_ID LEFT OUTER JOIN t_codes B ON B.F_CATID = 'C6' AND A.F_STYLE = B.F_ID
LEFT JOIN t_devstate J ON A.F_ID = J.F_DEVID AND J.F_STATEID IN ('1001','2001') LEFT JOIN t_states K ON J.F_STATEID = K.F_STATEID AND J.F_VALUE = K.F_VALUE
, t_branch D_1, t_branch D_2,t_branch D_3
A.F_DEPT=D_1.F_ID AND D_1.F_DEPT=D_2.F_ID AND D_2.F_DEPT=D_3.F_ID
AND A.F_ID = #{fId}
AND D_1.F_DEPTID LIKE #{fDeptid}||'%'
AND A.F_TYPE = #{fType}
AND A.F_MARK = #{fMark}
AND A.F_SPC = #{fSpc}
AND J.F_VALUE IN
${fState}
