mapper.select:
SELECT A.F_DATE, A.F_TIME, A.F_DEVID, A.F_DEPT, B.F_NAME WDNAME, C.F_NAME ZHNAME, D.F_NAME FHNAME, Z.F_NAME TRCD, A.F_ACNO, A.F_ACNO2, A.F_CDNO, A.F_IDNO, A.F_USRNO, concat('',A.F_TRAM) F_TRAM, A.F_DEVLS, A.F_RCPTNO, A.F_CHKNO, A.F_ACCTNO, A.F_MEMO, A.F_TERM, A.F_OPERATOR, CASE WHEN A.F_ACCTOFF='1' THEN '是' WHEN A.F_ACCTOFF='0' THEN '否' END AS F_ACCTOFF, CASE WHEN Y.F_NAME IS NOT NULL THEN Y.F_NAME ELSE A.F_RETNO END AS F_RETNO, CASE WHEN A.F_DATE IS NOT NULL AND A.F_DATE !='' THEN CONVERT(CONCAT(SUBSTR(A.F_DATE,1,4),'-',SUBSTR(A.F_DATE,5,2),'-',SUBSTR(A.F_DATE,7,2)),CHAR) END AS F_DATE1, CASE WHEN A.F_TIME IS NOT NULL AND A.F_TIME !='' THEN CONVERT(CONCAT(SUBSTR(A.F_TIME,1,2),':',SUBSTR(A.F_TIME,3,2)),CHAR) END AS F_TIME1 FROM (SELECT * FROM t_mtrans UNION SELECT * FROM ht_mtrans) A LEFT OUTER JOIN t_codes Z ON Z.F_CATID='JY' AND A.F_TRCD=Z.F_ID LEFT OUTER JOIN t_codes Y ON Y.F_CATID='RS' AND A.F_RETNO=Y.F_ID, t_branch B, t_branch C, t_branch D
A.F_DATE >= #{startdate}
AND A.F_DATE <= #{enddate}
AND A.F_TIME >= #{starttime}
AND A.F_TIME <= #{endtime}
AND B.F_DEPTID LIKE concat(#{deptid},"%")
AND A.F_DEPT=B.F_ID AND B.F_DEPT=C.F_ID AND C.F_DEPT=D.F_ID
AND A.F_ACNO = #{fAcno}
AND A.F_ACCTNO = #{fAcctno}
AND A.F_DEVID = #{fDevid}
AND A.F_IDNO = #{fIdno}
AND A.F_RETNO in ('00','0000','成功','000000')
AND (A.F_RETNO not in ('00','0000','成功','000000') OR A.F_RETNO IS NULL)
AND Z.F_NAME=#{fTrcd}
AND A.F_TERM = #{fTerm}
AND A.F_OPERATOR = #{fOperator}
ORDER BY ${orderby}
ORDER BY A.F_DATE DESC,A.F_TIME DESC

SELECT g.F_ID,g.F_NAME F_DEPTNAME, CASE WHEN f.fksum IS NULL THEN 0 ELSE f.fksum END fksum, CASE WHEN f.qysum IS NULL THEN 0 ELSE f.qysum END qysum, CASE WHEN f.sj_kksum IS NULL THEN 0 ELSE f.sj_kksum END sj_kksum, CASE WHEN f.sj_dxqysum IS NULL THEN 0 ELSE f.sj_dxqysum END sj_dxqysum, CASE WHEN f.sj_wyqysum IS NULL THEN 0 ELSE f.sj_wyqysum END sj_wyqysum, CASE WHEN f.sj_sjqysum IS NULL THEN 0 ELSE f.sj_sjqysum END sj_sjqysum, CASE WHEN f.sj_dsqysum IS NULL THEN 0 ELSE f.sj_dsqysum END sj_dsqysum FROM ( SELECT * FROM t_branch WHERE F_DEPTID LIKE CONCAT(#{fDeptid},'%')
AND F_LEVEL = #{fLevel}-1
AND F_LEVEL = 1
) g LEFT JOIN ( SELECT
B.F_ID,B.F_NAME F_DEPTNAME,
C.F_ID,C.F_NAME F_DEPTNAME,
D.F_ID,D.F_NAME F_DEPTNAME,
E.F_ID,E.F_NAME F_DEPTNAME,
sum(case A.F_TRCD WHEN 'ZZFK' THEN 1 ELSE 0 END ) fksum, sum(case A.F_TRCD WHEN 'ZZQY' THEN 1 ELSE 0 END ) qysum, sum(case A.F_TRCD WHEN 'O30021' THEN 1 ELSE 0 END ) sj_kksum, sum(case A.F_TRCD WHEN 'O57661' THEN 1 ELSE 0 END ) sj_dxqysum, sum(case A.F_TRCD WHEN '101002' THEN 1 ELSE 0 END ) sj_wyqysum, sum(case A.F_TRCD WHEN '171070' THEN 1 ELSE 0 END ) sj_sjqysum, sum(case A.F_TRCD WHEN '171170' THEN 1 ELSE 0 END ) sj_dsqysum FROM (SELECT * FROM t_mtrans UNION SELECT * FROM ht_mtrans) A LEFT OUTER JOIN t_codes Z ON Z.F_CATID='JY' AND A.F_TRCD=Z.F_ID LEFT OUTER JOIN t_codes Y ON Y.F_CATID='RS' AND A.F_RETNO=Y.F_ID, t_branch B, t_branch C, t_branch D, t_branch E
A.F_DATE >= #{startdate}
AND A.F_DATE <= #{enddate}
AND A.F_TIME >= #{starttime}
AND A.F_TIME <= #{endtime}
AND B.F_DEPTID LIKE concat(#{fDeptid},"%")
AND C.F_DEPTID LIKE concat(#{fDeptid},"%")
AND D.F_DEPTID LIKE concat(#{fDeptid},"%")
AND E.F_DEPTID LIKE concat(#{fDeptid},"%")
AND A.F_DEPT=B.F_ID AND B.F_DEPT=C.F_ID AND C.F_DEPT=D.F_ID AND D.F_DEPT=E.F_ID
AND A.F_ACNO = #{fAcno}
AND A.F_ACCTNO = #{fAcctno}
AND A.F_DEVID = #{fDevid}
AND A.F_IDNO = #{fIdno}
AND A.F_RETNO in ('00','0000','成功','000000')
AND (A.F_RETNO not in ('00','0000','成功','000000') OR A.F_RETNO IS NULL)
AND Z.F_NAME=#{fTrcd}
AND A.F_TERM = #{fTerm}
AND A.F_OPERATOR = #{fOperator}
GROUP BY
B.F_ID,B.F_NAME
C.F_ID,C.F_NAME
D.F_ID,D.F_NAME
E.F_ID,E.F_NAME
) f ON g.F_ID = f.F_ID

SELECT g.F_ID,g.F_NAME F_DEPTNAME,g.F_DEVID, CASE WHEN f.fksum IS NULL THEN 0 ELSE f.fksum END fksum, CASE WHEN f.qysum IS NULL THEN 0 ELSE f.qysum END qysum, CASE WHEN f.sj_kksum IS NULL THEN 0 ELSE f.sj_kksum END sj_kksum, CASE WHEN f.sj_dxqysum IS NULL THEN 0 ELSE f.sj_dxqysum END sj_dxqysum, CASE WHEN f.sj_wyqysum IS NULL THEN 0 ELSE f.sj_wyqysum END sj_wyqysum, CASE WHEN f.sj_sjqysum IS NULL THEN 0 ELSE f.sj_sjqysum END sj_sjqysum, CASE WHEN f.sj_dsqysum IS NULL THEN 0 ELSE f.sj_dsqysum END sj_dsqysum FROM ( SELECT a.F_ID F_DEVID,b.* FROM t_dev a LEFT JOIN t_branch b ON a.F_DEPT = b.F_ID
F_DEPTID LIKE CONCAT(#{fDeptid},'%')
AND F_LEVEL = #{fLevel}-1
AND F_LEVEL = 1
AND a.F_ID = #{fDevid}
) g LEFT JOIN ( SELECT
B.F_ID,B.F_NAME F_DEPTNAME,
C.F_ID,C.F_NAME F_DEPTNAME,
D.F_ID,D.F_NAME F_DEPTNAME,
E.F_ID,E.F_NAME F_DEPTNAME,
A.F_DEVID, sum(case A.F_TRCD WHEN 'ZZFK' THEN 1 ELSE 0 END ) fksum, sum(case A.F_TRCD WHEN 'ZZQY' THEN 1 ELSE 0 END ) qysum, sum(case A.F_TRCD WHEN 'O30021' THEN 1 ELSE 0 END ) sj_kksum, sum(case A.F_TRCD WHEN 'O57661' THEN 1 ELSE 0 END ) sj_dxqysum, sum(case A.F_TRCD WHEN '101002' THEN 1 ELSE 0 END ) sj_wyqysum, sum(case A.F_TRCD WHEN '171070' THEN 1 ELSE 0 END ) sj_sjqysum, sum(case A.F_TRCD WHEN '171170' THEN 1 ELSE 0 END ) sj_dsqysum FROM (SELECT * FROM t_mtrans UNION SELECT * FROM ht_mtrans) A LEFT OUTER JOIN t_codes Z ON Z.F_CATID='JY' AND A.F_TRCD=Z.F_ID LEFT OUTER JOIN t_codes Y ON Y.F_CATID='RS' AND A.F_RETNO=Y.F_ID, t_branch B, t_branch C, t_branch D, t_branch E
A.F_DATE >= #{startdate}
AND A.F_DATE <= #{enddate}
AND A.F_TIME >= #{starttime}
AND A.F_TIME <= #{endtime}
AND B.F_DEPTID LIKE concat(#{fDeptid},"%")
AND C.F_DEPTID LIKE concat(#{fDeptid},"%")
AND D.F_DEPTID LIKE concat(#{fDeptid},"%")
AND E.F_DEPTID LIKE concat(#{fDeptid},"%")
AND A.F_DEPT=B.F_ID AND B.F_DEPT=C.F_ID AND C.F_DEPT=D.F_ID AND D.F_DEPT=E.F_ID
AND A.F_ACNO = #{fAcno}
AND A.F_ACCTNO = #{fAcctno}
AND A.F_DEVID = #{fDevid}
AND A.F_IDNO = #{fIdno}
AND A.F_RETNO in ('00','0000','成功','000000')
AND (A.F_RETNO not in ('00','0000','成功','000000') OR A.F_RETNO IS NULL)
AND Z.F_NAME=#{fTrcd}
AND A.F_TERM = #{fTerm}
AND A.F_OPERATOR = #{fOperator}
GROUP BY
B.F_ID,B.F_NAME
C.F_ID,C.F_NAME
D.F_ID,D.F_NAME
E.F_ID,E.F_NAME
,
A.F_DEVID )f ON
g.F_ID = f.F_ID AND
g.F_DEVID = f.F_DEVID

SELECT count(*)
FROM
(SELECT *
FROM t_mtrans
UNION SELECT *
FROM ht_mtrans) A
LEFT OUTER JOIN t_codes Z ON Z.F_CATID='JY'
AND A.F_TRCD=Z.F_ID
LEFT OUTER JOIN t_codes Y ON Y.F_CATID='RS'
AND A.F_RETNO=Y.F_ID,
t_branch B,
t_branch C,
t_branch D


    A.F_DATE >= #{startdate}


    AND A.F_DATE <= #{enddate}


    AND A.F_TIME >= #{starttime}


    AND A.F_TIME <= #{endtime}


    AND B.F_DEPTID LIKE concat(#{deptid},"%")


    AND A.F_DEPT=B.F_ID
    AND B.F_DEPT=C.F_ID
    AND C.F_DEPT=D.F_ID


    AND A.F_ACNO = #{fAcno}


    AND A.F_ACCTNO = #{fAcctno}


    AND A.F_DEVID = #{fDevid}


